mapper.insert:
 INSERT INTO MP_MEMBER(   USERID 
					       , USERPASS 
					       , USERNAME 	)
               VALUES(    #{userId} 
              		   , #{userPass}
              		   , #{userName})

mapper.select:
SELECT USERID, USERPASS 
  FROM MP_MEMBER
 WHERE USERID = #{userId}
   AND USERPASS = #{userPass}

mapper.update:
UPDATE MP_MEMBER SET 
USERPASS = #{userPass},
USERNAME = #{userName}
WHERE USERID = #{userId}

mapper.delete:
	DELETE FROM MP_MEMBER
	WHERE USERID = #{userId}
  	AND USERPASS = #{userPass}
